import edu.duke.*;
public class findDna {
    public static void main(String[] args){
        String g = "";
        StorageResource res = getAllGenes(g);
        
        
        System.out.println(g.split("CTG", -1).length-1);
        // for (String s: res.data()){
            // System.out.println(s);
        // }
        
    }
    
    
    public static String findGeneSimple(String dna){
        String res = "";
        int startIndex = dna.indexOf("ATG");
        if (startIndex==-1) return "";
        int stopIndex = dna.indexOf("TAA", startIndex+3);
        if (stopIndex ==-1) return "";
        res = dna.substring(startIndex, stopIndex+3);
        if(res.length()%3!=0) return "";
        return res;
    }
    
    public static int findStopCodon(String dna, int startIndex, String codon){
    int currIndex = dna.indexOf(codon, startIndex+3);
    while(currIndex !=-1){
        int diff = currIndex - startIndex;
        if (diff%3==0)return currIndex;
        else currIndex = dna.indexOf(codon, currIndex+1);
    }
    return -1;
    }
    
        public static String findGene(String dna, int where){
        String res = "";
        int startIndex = dna.indexOf("ATG", where);
        if (startIndex==-1) return "";
        
        int taaIndex = findStopCodon(dna, startIndex, "TAA");
        int tagIndex = findStopCodon(dna, startIndex, "TAG");
        int tgaIndex = findStopCodon(dna, startIndex, "TGA");
        
        int minIndex = 0;
        if(taaIndex ==-1 || (tgaIndex!=-1 && tgaIndex < taaIndex)) minIndex = tgaIndex;
        else minIndex = taaIndex;
        
        if (minIndex ==-1 || (tagIndex!=-1 && tagIndex < minIndex)) minIndex = tagIndex;
        
        if (minIndex==-1) return "";
        

        res = dna.substring(startIndex, minIndex+3);
        if(res.length()%3!=0) return "";
        
 
        return res;
    }
    
    public static StorageResource getAllGenes(String dna){
    int start = 0;
    StorageResource res = new StorageResource();
    while (true){
    String currGene = findGene(dna, start);
    if(currGene.isEmpty()) break;
    res.add(currGene);
    start = dna.indexOf(currGene, start) + currGene.length();
    }
    return res;
    }
 
}
